  margin: 0;
  padding: 0;
  display: flex;
  flex-direction: column;
  border: 1px solid black;
  width: 50vw;
  height: 100vh;
`

const Div = styled.div`
display: flex;
`

const NameDiv = styled.input`
  width: 20%;
`

const MessageDiv = styled.input`
  width: 70%;
`
const ButtonDiv = styled.button`
  width: 30%;
`


